INSERT INTO `ldhkj_recruit` VALUES ('11','null','销售经理[北京]','2','null','<p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><strong>岗位职责</strong>：</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、根据公司大视频行业拓展规划，开拓广电、互联网、安防等企业级视频方向的用户市场；</span> &nbsp;<br/></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、接受公司产品与市场培训，向潜在用户宣讲和推广公司与产品、挖掘项目机会；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、对项目进度和执行状态进行有效控制；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 4、制定季度与年度销售计划，定期提交销售项目状态汇报与把控思路；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 5、跟进合同签订及应收账款催收。</span></p><p><br/><br/></p><p><strong style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">任职资格</strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">：</span><br/></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、大专及以上学历；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、至少1年以上销售相关领域工作经验；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、熟悉项目管理，对商务、投标运作流程有实践；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 4、较强的亲和力和表达能力，较好的气质和谈吐，思维敏捷，具有较高的商务谈判技巧；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 5、有良好的职业道德观，能够承受较强的工作压力。</span></p><p><br/></p>','null','19','4');
INSERT INTO `ldhkj_recruit` VALUES ('10','null','教育市场销售经理[北京]','1','null','<p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; line-height: 24px; background: rgb(255, 255, 255);\"><span style=\"color: rgb(119, 119, 119); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">岗位职责：</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; line-height: 24px; background: rgb(255, 255, 255);\"><span style=\"font-size: 14px;\"><span style=\"font-size: 14px;\">&nbsp;&nbsp;</span><span style=\"color: rgb(119, 119, 119); font-family: 微软雅黑; font-size: 14px;\">1、结合公司技术和产品体系，针对教育市场进行市场分析，提出合理的市场与产品方向；</span></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; line-height: 24px; background: rgb(255, 255, 255);\"><span style=\"font-size: 14px;\">&nbsp; <span style=\"font-family: 微软雅黑; color: rgb(119, 119, 119); font-size: 14px;\">2、负责洽谈拓展高校业务，维护与学校领导及学校的良好合作关系；</span></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; line-height: 24px; background: rgb(255, 255, 255);\"><span style=\"font-size: 14px;\">&nbsp; <span style=\"color: rgb(119, 119, 119); font-family: 微软雅黑; font-size: 14px;\">4、开展销售拓展工作，并调用公司相关人员进行配合，完成销售任务。</span></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; line-height: 24px; background: rgb(255, 255, 255);\"><span style=\"color: rgb(119, 119, 119); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; line-height: 24px; background: rgb(255, 255, 255);\"><span style=\"color: rgb(119, 119, 119); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">任职要求：</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; line-height: 24px; background: rgb(255, 255, 255);\"><span style=\"font-size: 14px;\">&nbsp; <span style=\"color: rgb(119, 119, 119); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">1、本科及以上学历；</span></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; line-height: 24px; background: rgb(255, 255, 255);\"><span style=\"font-size: 14px;\">&nbsp; <span style=\"color: rgb(119, 119, 119); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">2、至少有3年教育行业的销售或售前经验；</span></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; line-height: 24px; background: rgb(255, 255, 255);\"><span style=\"font-size: 14px;\">&nbsp; <span style=\"color: rgb(119, 119, 119); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">3、熟悉教育市场视频方向的当前应用与发展趋势；</span></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; line-height: 24px; background: rgb(255, 255, 255);\"><span style=\"font-size: 14px;\">&nbsp; <span style=\"color: rgb(119, 119, 119); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">4、拥有良好的文档撰写与PPT宣讲能力；</span></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; line-height: 24px; background: rgb(255, 255, 255);\"><span style=\"font-size: 14px;\">&nbsp; <span style=\"color: rgb(119, 119, 119); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">5、能适应出差。</span></span></p><p><span style=\"font-size: 14px;\">&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color: rgb(119, 119, 119); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><br/></span></span></p><p><br/></p>','null','1','4');
INSERT INTO `ldhkj_recruit` VALUES ('9','null','渠道拓展经理[北京]','1','null','<p style=\"text-align: left;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><strong>岗位职责</strong>：</span></p><p style=\"text-align: left;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、负责广电或相关视频行业渠道拓展工作；</span></p><p style=\"text-align: left;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、挖掘、维系各区域代理商，保持良好合作与市场推广，与代理商一起对其重点项目进行销售支持；</span></p><p style=\"text-align: left;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、针对部分重点客户进行销售跟踪与项目落单推进，维系客户关系；</span></p><p style=\"text-align: left;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 4、担负管辖区域的业绩考核，同时获得与之相匹配的丰厚提成与奖金回报。</span></p><p style=\"text-align: left;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp;</span></p><p style=\"text-align: left;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><strong>任职要求</strong>：</span></p><p style=\"text-align: left;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、大专以上学历；</span></p><p style=\"text-align: left;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、对广电或视频行业的全国经销商体系有充分认识者优先；</span></p><p style=\"text-align: left;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、具备行业渠道拓展或售前或实施相关经验者优先；</span></p><p style=\"text-align: left;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 4、善于交流和团队配合，能适应出差。</span></p><p><br/></p>','null','5','4');
INSERT INTO `ldhkj_recruit` VALUES ('12','null','市场技术经理[北京]','1','null','<p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><strong>岗位职责</strong>：</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、负责公司展会推广的现场搭建、演示和推广工作，配合品牌总监最好线下展会演示技术工作；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、负责对渠道代理进行产品售前培训与实施培训，对代理商部分项目进行售前支持和实施调试与售后支持；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、定期梳理反馈大视频行业各细分市场需求，一起完善针对各细分市场的推广与产品运维资料，确保市场资料产品化；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 4、配合公司针对细分市场的市场调研和产品调研工作。</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><strong>任职要求</strong>：</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、大专及以上学历；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、拥有1~3年视频行业实施、市场推广经验；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、对视频转码、内容生产、视频云服务有一定了解；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 4、拥有良好的PPT宣讲、用户交流能力；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 5、能适应出差；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 6、拥有广电/教育等视频行业的厂商或集成商的从业经验者优先。</span></p><p><br/></p>','null','4','3');
