INSERT INTO `ldhkj_recruit` VALUES ('16','null','售前经理[杭州]','1','null','<p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><strong>岗位职责</strong>：</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、完成售前方案与技术投标工作；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、与相关产品、实施、研发人员密切沟通，推动售前项目可行性评估与执行；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、配合直接领导部门完成方案产品化和模板工作；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 4、配合公司进行市场调研和产品调研工作。<br/><br/><strong>任职要求</strong>：</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、专科以上学历，有计算机相关专业优先；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、至少1年的视频行业售前经验；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、对视频转码、内容生产、视频云服务有一定了解；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 4、拥有良好的文档撰写与PPT宣讲能力；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 5、能适应经常出差，能接受加班压力；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 6、拥有广电/视频行业的厂商或集成商的从业经验者优先。</span></p><p><br/></p>','null','9','2');
INSERT INTO `ldhkj_recruit` VALUES ('17','null','产品经理[杭州]','3','null','<p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><strong>岗位职责</strong>：</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、负责产品功能的详细定义工作，包括功能点的细化分解、应用场景的说明、相关文档的编写等工作；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、负责推进产品的内部实现以及在产品实现过程中对开发、测试人员进行管理、沟通和协调工作；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、负责项目管理流程优化工作。</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><strong>任职要求</strong>：</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、本科以上学历，有计算机、软件工程或管理类相关专业优先；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、至少1年软件产品的项目管理经验；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、具备一定的音视频基础知识，有广电编转码产品从业经验者优先；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 4、较强的团队管理和协调能力，且具有独立开展工作的能力。</span></p><p><br/></p>','null','10','2');
INSERT INTO `ldhkj_recruit` VALUES ('18','null','软件测试工程师[杭州]','3','null','<p><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">岗位职责：</span></strong></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><strong>&nbsp;&nbsp;</strong>1、参与需求和设计评审，根据项目测试负责人的任务分配，编写和执行测试用例，提交问题和测试报告；</span></p><p>&nbsp;&nbsp;<span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">2、与关联部门进行沟通，协助开发工程师解决开发过程中的技术问题，协助实施人员重现定位现场客户问题；</span></p><p>&nbsp;&nbsp;<span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">3、协助项目测试负责人和部门负责人推行、改进测试方法、流程、提高测试技术等；</span></p><p>&nbsp;&nbsp;<span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">4、配合团队成员完成其他测试工作。</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp;</span></p><p><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">任职要求：</span></strong></p><p>&nbsp;&nbsp;<span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">1、本科及以上学历，计算机及相关专业优先；</span></p><p>&nbsp;&nbsp;<span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">2、至少2年以上软件功能试经验；</span></p><p>&nbsp;&nbsp;<span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">3、熟悉Linux命令，掌握mysql/tomcat等的配置，能独立搭建测试环境；</span></p><p>&nbsp; <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">4、有脚本语言的实践经验者优先，如python，perl或shell；</span></p><p>&nbsp; <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">5、有视频相关知识和测试经验者优先。</span></p><p><br/></p>','null','11','1');
INSERT INTO `ldhkj_recruit` VALUES ('19','null','测试开发工程师[杭州]','1','null','<p><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">岗位职责：</span></strong></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、负责持续集成改进，设计和开发自动化框架或平台；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、负责接口测试和UI测试，推进整个项目组的自动化测试流程；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、调研评估新的测试技术和工具，并且应用和推广。</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp;</span></p><p><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">任职要求：</span></strong></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、本科及以上学历，计算机及相关专业优先；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、至少2年以上从事自动化脚本开发测试经验；1年以上jenkins的项目开发实施经验；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、有丰富的接口、业务测试经验，从可测试性的角度去思考被测的模块和接口，与开发有效沟通；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 4、掌握Java/Python/Ruby中至少一种语言。</span></p><p><br/></p>','null','12','1');
INSERT INTO `ldhkj_recruit` VALUES ('20','null','Java开发工程师[杭州]','若干','null','<p><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">岗位职责：</span></strong></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、参与系统架构设计、接口规范制定、技术文档编写等；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、改进现有模块功能，提高系统的健壮性，稳定性以及性能；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、全栈培养方向：前端会涉及H5或Flash播放器开发。</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp;</span></p><p><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">任职要求：</span></strong></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、本科以上学历，计算机及相关专业；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、至少3年网站后端开发或维护经验，或2年互联网网站独立架构设计经验；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、熟悉J2EE体系架构，熟悉设计模式，熟悉Java主流开发框架（如：Spring，SpringMVC，CXF等）；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 4、熟悉MySql数据库，iBATIS或MyBatis；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 5、熟悉NoSql产品，如Cassandra，Redis，Memcached；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 6、具有分布式应用架构经验，和分布式消息队列 （RabbitMQ等）经验；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 7、熟悉并发编程、NIO，以及常用的网络编程框架（如：Netty）；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 8、有AWS，阿里云使用经验者优先；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 9、有HTML，CSS，JS（JQuery）使用经验者优先。</span></p><p><br/></p>','null','18','1');
INSERT INTO `ldhkj_recruit` VALUES ('21','null','Android开发工程师[杭州]','1','null','<p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><strong>岗位职责</strong>：</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、Android客户端应用开发；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、适配手机/平板/机顶盒等各类终端设备；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、能够快速、高效的开发产品；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 4、持续优化产品性能。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><strong>任职要求</strong>：</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、本科以上学历，计算机及相关专业；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、至少2年Android开发经验, 有完整企业级项目开发经验；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、熟练使用Java语言；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 4、熟悉Android系统的各类接口与特性, 熟悉各种调试和优化工具；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 5、精通Android下的高性能编程与优化。</span></p><p><br/></p>','null','13','1');
INSERT INTO `ldhkj_recruit` VALUES ('22','null','iOS开发工程师[杭州]','1','null','<p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><strong>岗位职责</strong>：</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、iOS客户端应用开发；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、熟悉并追踪各个iOS版本的新特性；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、能够快速、高效的开发产品；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 4、持续优化产品性能。</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><strong>任职要求</strong>：</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、本科及以上学历，计算机及相关专业；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、至少2年iOS开发经验, 有完整企业级项目开发经验；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、熟练使用Objective-C语言；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 4、熟悉iOS系统的各类接口与特性，熟悉各种开发调试工具；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 5、精通iOS高性能编程与优化。</span></p><p><br/></p>','null','14','1');
INSERT INTO `ldhkj_recruit` VALUES ('23','null','流媒体开发工程师[杭州]','1','null','<p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><strong>工作职责</strong>: &nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、研发稳定高质量的音视频传输分发技术；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、研发低延时高可用数据传输方案；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、利用现有技术, 设计测试方案, 对技术方案不断测试并进行优化。</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><strong>任职要求</strong>：</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、本科及以上学历，计算机及相关专业；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、至少2年的音视频实时通讯/流媒体服务开发经验；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、Linux环境开发熟练，掌握一到两种脚本语言；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 4、精通C语言及SOCKET开发；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 5、熟悉音视频数据传输格式；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 6、有流媒体网络传输优化经验者优先；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 7、或有视频会议, 双向视频通话相关项目经验者优先；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 8、或熟悉WebRTC框架者优先。</span></p><p><br/></p>','null','15','1');
INSERT INTO `ldhkj_recruit` VALUES ('24','null','视频图像算法研发工程师[杭州]','若干','null','<p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><strong>岗位职责</strong>：</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、研究视频图像的各种增强效果算法，如去噪、去模糊、去块效应、去隔行效果、去抖动、提高清晰度、超分辨率转换、提高亮度等；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、优化图像算法，提升画质与性能，实现可产品化；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、参与算法、专利等文档撰写。</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><strong>任职要求</strong>：</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、至少本科毕业3年或研究生毕业2年的相关工作经验，有图像处理算法研究经验；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、具有很强的创新研究精神，关注国内外优秀论文，有较强的英语阅读能力；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、对目前业界先进的图像处理技术有较深入的理解；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 4、精通C/C++、Matlab，熟悉深度学习等机器学习理论。</span></p><p><br/></p>','null','17','1');
INSERT INTO `ldhkj_recruit` VALUES ('25','null','视频优化工程师[杭州]','若干','null','<p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><strong>岗位职责</strong>：</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、优化视频前处理以及视频编码性能，平台涉及x86平台,GPU（NVIDIA CUDA）；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、移植和优化视频编码，处理模块到移动平台，对性能模块使用ARM/NEON指令集优化。</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><strong>任职要求</strong>：</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、本科及以上学历，计算机及相关专业；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、有X86、GPU或ARM优化经验。</span></p><p><br/></p>','null','16','1');
INSERT INTO `ldhkj_recruit` VALUES ('26','null','视频编码算法研究工程师[杭州]','若干','null','<p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><strong>岗位职责</strong>：</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、研究H.264/H.265等编码内核算法，如运动估计，模型选择，码率控制；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、整体提升H.264/H.265等编码器压缩效率以及编码性能，提升竞争力。</span></p><p><br/></p><p><strong style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">任职要求</strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">：</span><br/></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 1、至少有3年相关工作经验，或应届硕士研究生、本科生有实操项目经验；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 2、理解视频压缩原理，熟悉H.264、H.265等视频压缩标准，有相关项目经验者优先；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 3、精通Windows/Linux C/C++开发，熟悉多线程编程以及代码性能优化；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp; 4、最好掌握汇编优化，GPGPU并行运算。</span></p><p><br/></p>','null','2','1');
