INSERT INTO `ldhkj_solution` VALUES ('179','151','250','应用场景','<p style=\"line-height: 2em;\"><img src=\"/ueditor/php/upload/image/20170727/1501134741689056.jpg\" title=\"1501134741689056.jpg\" alt=\"1501134741689056.jpg\" width=\"692\" height=\"389\"/></p><ol class=\" list-paddingleft-2\" style=\"list-style-type: decimal;\"><li><p style=\"text-indent: 0px; line-height: 2em;\"><strong><span style=\"font-size: 14px;line-height: 150%;font-family: 微软雅黑, sans-serif;background: white\">公有云现场多形态直播</span></strong></p><p style=\"text-indent: 0px; line-height: 2em;\"><strong><span style=\"font-size: 14px;line-height: 150%;font-family: 微软雅黑, sans-serif;background: white\"></span></strong><span style=\"font-size: 14px;line-height: 150%;font-family: 微软雅黑, sans-serif;background: white\">互联网直播、活动外拍、主持人手机采集等情况下，通过手机客户端的推流功能，实时将直播画面上传当虹云平台；手机推流端还可以实现美颜、磨皮、画质优化、连麦等效果；专业摄像机以及其他信号通过在线编转码推流，上传到当虹云平台，云平台做智能 CDN 分发支撑多屏播放和内容交易分享。</span></p></li><li><p style=\"text-indent: 0px; line-height: 2em;\"><strong><span style=\"font-size: 14px;line-height: 150%;font-family: 微软雅黑, sans-serif;background: white\">私有云视频处理能力平台</span></strong></p><p style=\"text-indent: 0px; line-height: 2em;\"><strong><span style=\"font-size: 14px;line-height: 150%;font-family: 微软雅黑, sans-serif;background: white\"></span></strong><span style=\"font-size: 14px;line-height: 150%;font-family: 微软雅黑, sans-serif;background: white\">专业摄像机信号以及高清摄像头等信号通过转码推流，上传到私有云平台，可通过私有云多屏转码系统，进行多屏转码分发；信号通过接收解码，可以再次输出 SDI 信号进播控系统做频道播出；可进行内容生产加工，用于运营服务；私有云提供内容管理、智能识别、视频监看等能力系统，可为用户提供全方位视频处理及内容管理服务。</span></p></li></ol><p><br/></p>','1500307200','null');
